import styled from 'styled-components';
const Container = styled('div')`
  border: 1px solid #000;
  width: 400px;
  padding: 20px;
  margin-left: 30px;
  .form {
    display: flex;
    flex-direction: column;
    gap: 15px;
  }
`;
export default Container;
